<h2 id="代码">代码</h2>
<p>废话不多说直接上代码👑</p>
<div class="highlight"><pre style="color:#f8f8f2;background-color:#272822;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-c" data-lang="c"><span style="color:#75715e">/*
</span><span style="color:#75715e">标准体重计算器
</span><span style="color:#75715e">By WeepingDogel
</span><span style="color:#75715e">
</span><span style="color:#75715e">世卫计算方法
</span><span style="color:#75715e">男性：(身高cm－80)×70﹪=标准体重
</span><span style="color:#75715e">女性：(身高cm－70)×60﹪=标准体重
</span><span style="color:#75715e">标准体重正负10﹪为正常体重
</span><span style="color:#75715e">标准体重正负10﹪~ 20﹪为体重过重或过轻
</span><span style="color:#75715e">标准体重正负20﹪以上为肥胖或体重不足
</span><span style="color:#75715e">超重计算公式
</span><span style="color:#75715e">超重%=[（实际体重－理想体重）/（理想体重）]×100%
</span><span style="color:#75715e">*/</span>
<span style="color:#75715e">/*导入标准库stdio.h （必须）*/</span>
<span style="color:#75715e">#include</span> <span style="color:#75715e">&lt;stdio.h&gt;</span><span style="color:#75715e">
</span><span style="color:#75715e"></span><span style="color:#75715e">/*标准体重*/</span>
<span style="color:#66d9ef">int</span> weight;
<span style="color:#75715e">/*声明函数*/</span>
<span style="color:#66d9ef">float</span> <span style="color:#a6e22e">judge</span>(<span style="color:#66d9ef">float</span> parameter1,<span style="color:#66d9ef">float</span> parameter2);
<span style="color:#66d9ef">float</span> <span style="color:#a6e22e">Count_M</span>(<span style="color:#66d9ef">float</span> parameter);
<span style="color:#66d9ef">float</span> <span style="color:#a6e22e">Count_F</span>(<span style="color:#66d9ef">float</span> parameter);
<span style="color:#75715e">/*声明主函数main() (这个都不写，那写个屁)*/</span>
<span style="color:#66d9ef">int</span> <span style="color:#a6e22e">main</span>()
{
    printf(<span style="color:#e6db74">&#34;欢迎使用.&#34;</span>);
    <span style="color:#75715e">/*身高*/</span>
    <span style="color:#66d9ef">int</span> height;
    <span style="color:#75715e">/*声明变量sex表示性别*/</span>
    <span style="color:#66d9ef">char</span> sex;
    <span style="color:#75715e">/*声明变量：实际体重*/</span>
    <span style="color:#66d9ef">int</span> real_weight;
    <span style="color:#75715e">/*提示用户输入性别*/</span>
    printf(<span style="color:#e6db74">&#34;请输入你的性别</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">（M为男性,F为女性,注意大小写）&#34;</span>);
    printf(<span style="color:#e6db74">&#34;</span><span style="color:#ae81ff">\n\n</span><span style="color:#e6db74">:&#34;</span>);
    <span style="color:#75715e">/*读取用户输入的数据并赋值给sex*/</span>
    scanf(<span style="color:#e6db74">&#34;%c&#34;</span>,<span style="color:#f92672">&amp;</span>sex);
    <span style="color:#75715e">/*进入判断，并判断变量sex的值是否为M*/</span>
    <span style="color:#66d9ef">if</span>(sex <span style="color:#f92672">==</span> <span style="color:#e6db74">&#39;M&#39;</span>)
    {
        <span style="color:#75715e">/*当变量sex=M时，判断为男性，将执行以下代码*/</span>
        printf(<span style="color:#e6db74">&#34;你是男性&#34;</span>);
        printf(<span style="color:#e6db74">&#34;输入你的身高(单位:厘米)&#34;</span>);
        printf(<span style="color:#e6db74">&#34;</span><span style="color:#ae81ff">\n\n</span><span style="color:#e6db74">:&#34;</span>);
        scanf(<span style="color:#e6db74">&#34;%d&#34;</span>,<span style="color:#f92672">&amp;</span>height);
        weight <span style="color:#f92672">=</span> (<span style="color:#66d9ef">int</span>) Count_M((<span style="color:#66d9ef">float</span>) height);
        printf(<span style="color:#e6db74">&#34;你的身高对应的标准体重为%dkg</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>,weight);
        printf(<span style="color:#e6db74">&#34;请输入你的实际体重(单位:kg)&#34;</span>);
        printf(<span style="color:#e6db74">&#34;</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">:&#34;</span>);
        scanf(<span style="color:#e6db74">&#34;%d&#34;</span>,<span style="color:#f92672">&amp;</span>real_weight);
        judge((<span style="color:#66d9ef">float</span>) real_weight,(<span style="color:#66d9ef">float</span>) weight);
    }
    <span style="color:#75715e">/*进入判断，并判断变量sex的值是否为F*/</span>
    <span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(sex <span style="color:#f92672">==</span> <span style="color:#e6db74">&#39;F&#39;</span>)
    {
        <span style="color:#75715e">/*当变量sex=F时，判断为女性，将执行以下代码*/</span>
        printf(<span style="color:#e6db74">&#34;你是女性&#34;</span>);
        printf(<span style="color:#e6db74">&#34;输入你的身高(单位:厘米)&#34;</span>);
        printf(<span style="color:#e6db74">&#34;</span><span style="color:#ae81ff">\n\n</span><span style="color:#e6db74">:&#34;</span>);
        scanf(<span style="color:#e6db74">&#34;%d&#34;</span>,<span style="color:#f92672">&amp;</span>height);
        weight <span style="color:#f92672">=</span> (<span style="color:#66d9ef">int</span>) Count_F((<span style="color:#66d9ef">float</span>) height);
        printf(<span style="color:#e6db74">&#34;你的身高对应的标准体重为%dkg</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">&#34;</span>,weight);
        printf(<span style="color:#e6db74">&#34;请输入你的实际体重(单位:kg)&#34;</span>);
        printf(<span style="color:#e6db74">&#34;</span><span style="color:#ae81ff">\n</span><span style="color:#e6db74">:&#34;</span>);
        scanf(<span style="color:#e6db74">&#34;%d&#34;</span>,<span style="color:#f92672">&amp;</span>real_weight);
        judge((<span style="color:#66d9ef">float</span>) real_weight,(<span style="color:#66d9ef">float</span>) weight);
    }
    <span style="color:#66d9ef">return</span> <span style="color:#ae81ff">0</span>;
}
<span style="color:#75715e">/*计算出男性标准体重*/</span>
<span style="color:#66d9ef">float</span> <span style="color:#a6e22e">Count_M</span>(<span style="color:#66d9ef">float</span> parameter)
{
    <span style="color:#75715e">/*男性：(身高cm－80)×70﹪=标准体重*/</span>
    <span style="color:#75715e">/*定义变量:结果*/</span>
    <span style="color:#66d9ef">float</span> result;
    result <span style="color:#f92672">=</span> (<span style="color:#66d9ef">int</span>)(parameter <span style="color:#f92672">-</span> <span style="color:#ae81ff">80.0</span>) <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.7</span>;

    <span style="color:#66d9ef">return</span> result;
}
<span style="color:#75715e">/*计算出女性标准体重*/</span>
<span style="color:#66d9ef">float</span> <span style="color:#a6e22e">Count_F</span>(<span style="color:#66d9ef">float</span> parameter)
{
    <span style="color:#75715e">/*女性：(身高cm－70)×60﹪=标准体重*/</span>
    <span style="color:#75715e">/*定义变量:结果*/</span>
    <span style="color:#66d9ef">float</span> result;
    result <span style="color:#f92672">=</span> (<span style="color:#66d9ef">int</span>)(parameter <span style="color:#f92672">-</span> <span style="color:#ae81ff">70.0</span>) <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.6</span>;

    <span style="color:#66d9ef">return</span> result;
}
<span style="color:#75715e">/*检测实际体重是否符合标准*/</span>
<span style="color:#66d9ef">float</span> <span style="color:#a6e22e">judge</span>(<span style="color:#66d9ef">float</span> parameter1,<span style="color:#66d9ef">float</span> parameter2)
{
    <span style="color:#75715e">/*标准体重正负10﹪为正常体重*/</span>
    <span style="color:#66d9ef">if</span>(parameter1 <span style="color:#f92672">&lt;=</span> parameter2 <span style="color:#f92672">+</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.1</span> <span style="color:#f92672">&amp;&amp;</span> parameter1 <span style="color:#f92672">&gt;=</span> parameter2 <span style="color:#f92672">-</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.1</span>)
    {
        printf(<span style="color:#e6db74">&#34;你的体重符合正常标准&#34;</span>);
    }
    <span style="color:#75715e">/*标准体重正负10﹪~ 20﹪为体重过重或过轻*/</span>
    <span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(parameter1 <span style="color:#f92672">&gt;=</span> parameter2 <span style="color:#f92672">+</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.1</span> <span style="color:#f92672">&amp;&amp;</span> parameter1 <span style="color:#f92672">&lt;=</span> parameter2 <span style="color:#f92672">+</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.2</span>)
    {
        printf(<span style="color:#e6db74">&#34;你的体重过重&#34;</span>);
    }
    <span style="color:#75715e">/*标准体重正负10﹪~ 20﹪为体重过重或过轻*/</span>
    <span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(parameter1 <span style="color:#f92672">&lt;=</span> parameter2 <span style="color:#f92672">-</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.1</span> <span style="color:#f92672">&amp;&amp;</span> parameter1 <span style="color:#f92672">&gt;=</span> parameter2 <span style="color:#f92672">-</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.2</span>)
    {
        printf(<span style="color:#e6db74">&#34;你的体重过轻&#34;</span>);
    }
    <span style="color:#75715e">/*标准体重正负20﹪以上为肥胖或体重不足*/</span>
    <span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(parameter1 <span style="color:#f92672">&gt;</span> parameter2 <span style="color:#f92672">+</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.2</span>)
    {
        printf(<span style="color:#e6db74">&#34;你太胖了。&#34;</span>);
    }
    <span style="color:#75715e">/*标准体重正负20﹪以上为肥胖或体重不足*/</span>
    <span style="color:#66d9ef">else</span> <span style="color:#66d9ef">if</span>(parameter1 <span style="color:#f92672">&lt;</span> parameter2 <span style="color:#f92672">-</span> parameter2 <span style="color:#f92672">*</span> <span style="color:#ae81ff">0.2</span>)
    {
        printf(<span style="color:#e6db74">&#34;你太瘦了。&#34;</span>);
    }
    <span style="color:#66d9ef">return</span> <span style="color:#ae81ff">0</span>;
}
